},
{
  "key": "ctrl+shift+4",
  "command": "md-shortcut.toggleTitleH4",
  "when": "editorTextFocus && markdownShortcuts:enabled && resourceExtname == .md"
},
{
  "key": "ctrl+shift+5",
  "command": "md-shortcut.toggleTitleH5",
  "when": "editorTextFocus && markdownShortcuts:enabled && resourceExtname == .md"
},
{
  "key": "ctrl+shift+6",
  "command": "md-shortcut.toggleTitleH6",
  "when": "editorTextFocus && markdownShortcuts:enabled && resourceExtname == .md"
},
{
  "key": "ctrl+i",
  "command": "md-shortcut.toggleItalic",
  "when": "editorTextFocus && markdownShortcuts:enabled && resourceExtname == .md"
},
{
  "key": "ctrl+b",
  "command": "md-shortcut.toggleBold",
  "when": "editorTextFocus && markdownShortcuts:enabled && resourceExtname == .md"
},


{
  "key": "alt+e c",
  "command": "workbench.files.action.collapseExplorerFolders"
},


// --- Bash ---
{
  "key": "F1",
  "command": "workbench.action.terminal.runActiveFile", 
  "when": "resourceLangId == shellscript && remoteName == wsl"
},

// --- AutoHotkey ---

{
  "key": "f1",
  "command": "workbench.action.terminal.sendSequence",
  "args": {
    "text": "C:\\Users\\me\\AppData\\Local\\Programs\\AutoHotkey\\v2\\AutoHotkey64.exe ${file}\u000D"
  },
  "when": "editorLangId == 'ahk' || editorLangId == 'ahk2'"
},

{
  "key": "f1",
  "command": "-workbench.action.showCommands"
},
{
  "key": "f1",
  "command": "python.execInTerminal-icon",
  "when": "editorTextFocus && editorLangId == 'python'"
},
{
  "key": "ctrl+f9",
  "command": "ahk++.run",
  "when": "shellExecutionSupported && editorLangId == 'ahk' || shellExecutionSupported && editorLangId == 'ahk2'"
},
{
  "key": "f1",
  "command": "ahk++.run",
  "when": "shellExecutionSupported && editorLangId == 'ahk' || shellExecutionSupported && editorLangId == 'ahk2'"
},
{
  "key": "ctrl+f9",
  "command": "-ahk++.run",
  "when": "shellExecutionSupported && editorLangId == 'ahk' || shellExecutionSupported && editorLangId == 'ahk2' || shellExecutionSupported && editorLangId == 'ahk' && editorLangId == 'ahk2'"
},
{
  "key": "ctrl+f2",
  "command": "bookmarks.toggle",
  "when": "editorTextFocus"
},
{
  "key": "ctrl+alt+k",
  "command": "-bookmarks.toggle",
  "when": "editorTextFocus"
},
{
  "key": "ctrl+alt+f2",
  "command": "editor.action.changeAll",
  "when": "editorTextFocus && !editorReadonly"
},
{
  "key": "ctrl+f2",
  "command": "-editor.action.changeAll",
  "when": "editorTextFocus && !editorReadonly"
},
{
  "key": "f2",
  "command": "bookmarks.jumpToNext",
  "when": "editorTextFocus"
},
{
  "key": "ctrl+alt+l",
  "command": "-bookmarks.jumpToNext",
  "when": "editorTextFocus"
},
{
  "key": "shift+f2",
  "command": "bookmarks.jumpToPrevious",
  "when": "editorTextFocus"
},
{
  "key": "ctrl+alt+j",
  "command": "-bookmarks.jumpToPrevious",
  "when": "editorTextFocus"
},
{
  "key": "ctrl+shift+f2",
  "command": "bookmarks.clear"
},
{
  "key": "alt+x",
  "command": "editor.action.smartSelect.expand",
  "when": "editorTextFocus"
},
{
  "key": "shift+alt+right",
  "command": "-editor.action.smartSelect.expand",
  "when": "editorTextFocus"
},
// --- NeoVim ---
{
  "key": "alt+n",
  "command": "vscode-neovim.restart",
  "when": "editorTextFocus"
},
{
  "key": "shift+alt+n",
  "command": "vscode-neovim.stop",
  "when": "editorTextFocus"
},
]"}
